import './app.css';
import React, { useState } from 'react';

const TrackProgress = ({ todo }) => {

}


function App() {
     const [todo, setTodo] = useState([
          {id: '974379439', content: 'my Task One', isDone: true},
          {id: '873498348', content: 'my Task One', isDone: true},
     ]);


     const [ value, setValue ] = useState('');
     console.log(todo)
     console.log(value);
     const trackProgress = () => {
          const notDone = todo.filter(listItem => listItem.isDone !== true);
          return (
               <p>{ notDone.length} remaining of { todo.length }</p>
          )
     }

     const handleSubmit = (e) => {
          if(!value) {
               return false
          };
          e.preventDefault();
          setTodo(...todo, { id: Math.random() * 1000, content: value, isDone: false })
     } 

     return (
          <div className="App">
               <input 
                    value={ value }
                    name='content'
                    onChange={(e) => setValue(e.target.value)}
               />
               <button onClick={ handleSubmit }>Click</button>
               { trackProgress() }
               <ul>
                    {todo.map(listItem => 
                         <li key={ listItem.id }>{ listItem.content }</li>     
                    )}
               </ul>
          </div>


     );
}

export default App;
