import './app.css';
import React, { useState } from 'react';

function App() {
     const [ todo, setTodo ] = useState([
          {id: 974379439, content: 'my Task One', isDone: true},
          {id: 873498348, content: 'my Task One', isDone: true},
     ]);

     const [ value, setValue ] = useState('');

     const trackProgress = () => {
          const notDone = todo.filter(listItem => listItem.isDone !== true);
          return (
               <p>{ notDone.length} remaining of { todo.length }</p>
          )
     }

     const handleChange = (e) => {
          const itemId = e.target.id;
          const item = todo.filter(listItem => listItem.id === itemId);
          item.isDone = !item.isDone
          setTodo([ ...todo, item])
     }

     const handleSubmit = (e) => {
          e.preventDefault();
          if(!value) {
               return false
          };
          setTodo([...todo, { id: Math.floor(Math.random() * 1000000), content: value, isDone: false }])
     } 

     return (
          <div className="App">
               <input 
                    value={ value }
                    name='content'
                    onChange={(e) => setValue(e.target.value)}
               />
               <button onClick={ handleSubmit }>Click</button>
               { trackProgress() }
               <ul>
                    {todo.map(listItem => 
                         <li key={ listItem.id } id={listItem.id} onClick={ handleChange } >{ listItem.content }</li>     
                    )}
               </ul>
          </div>


     );
}

export default App;
