import './app.css';
import React, { useState } from 'react';

const TrackProgress = ({ todo }) => {

}


function App() {
     const todo = [
          {id: '974379439', content: 'my Task One', isDone: true},
          {id: '873498348', content: 'my Task One', isDone: true},
     ];

     const [ value, setValue ] = useState('');

     const trackProgress = () => {
          const notDone = todo.filter(listItem => listItem.isDone !== true);
          return (
               <p>{ notDone.length} remaining of { todo.length }</p>
          )
     }

     const handleSubmit = (e) => {
          e.preventDefault();
          todo.push({ id: Math.random() * 1000, content: value, isDone: false })
     } 

     return (
          <div className="App">
               <input 
                    value={ value }
                    name='content'
                    onChange={(e) => setValue(e.target.value)}
               />
               <button onCLick={ handleSubmit }>Click</button>
               { trackProgress }
               <ul>
                    {todo.map(listItem => 
                         <li>{ listItem.content }</li>     
                    )}
               </ul>
          </div>


     );
}

export default App;
